
public class Whileloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i=1;
		
		while(i<=10)
		{
			System.out.println(i);
				i=i+3;
		}
		
		
		int j=10;
		
		while(j>=0)
		{
			
			if (!(j==5))
			{
				System.out.println("now i am in if statement loop   " +j);
			}
			
			j--;
			
		}
		
		

	}

}
